mapper.sql:
from resources , users where resources.create_user_id=users.user_id


	and resources_name like
	concat(#{condition.resourcesName,jdbcType=VARCHAR},'%')


	and resources_type =#{condition.resourcesType,jdbcType=VARCHAR}


	and resources_id =#{condition.resourcesId,jdbcType=VARCHAR}


	and parent_resources_id
	=#{condition.parentResourcesId,jdbcType=VARCHAR}

mapper.select:
select resources.RESOURCES_ID, resources.SEQUENCE,resources.PARENT_RESOURCES_ID, resources.RESOURCES_NAME, resources.RESOURCES_URL, resources.RESOURCES_PATH, resources.RESOURCES_ICON, resources.RESOURCES_TYPE, resources.CREATE_TIME, resources.CREATE_USER_ID, resources.REMARK, resources.STAUTS,users.name CREATE_USER_NAME,resources.syid
ORDER BY ${orderBy}
ORDER BY RESOURCES_ID
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
