mapper.select:
        select
        USERS.user_id code,
        USERS.name name
        
        
            
                ORDER BY ${orderBy}
            
            
                ORDER BY USERS.user_id
            
        

        limit #{start,jdbcType=DECIMAL},#{limit,jdbcType=DECIMAL}

mapper.update:
update USERS
set STAUTS = #{struts,jdbcType=CHAR}
where USER_ID = #{userId,jdbcType=VARCHAR}

update USERS
set password = #{password,jdbcType=CHAR}
where USER_ID = #{userId,jdbcType=VARCHAR}

update USERS
set STAUTS = 9
where USER_ID = #{userId,jdbcType=VARCHAR}

mapper.sql:
from USERS left join department s on USERS.department_id=s.department_id left join province s2 on users.area_code=s2.code where USERS.STAUTS='1'
and USERS.name like concat(#{condition.name,jdbcType=VARCHAR},'%')
and USERS.department_Id =#{condition.departmentId,jdbcType=VARCHAR}
and USERS.id_Card =#{condition.idCard,jdbcType=VARCHAR}
and USERS.mobile =#{condition.mobile,jdbcType=VARCHAR}
and USERS.area_code like concat(#{condition.areaCode,jdbcType=VARCHAR},'%')
and USERS.account =#{condition.account,jdbcType=VARCHAR}

mapper.select:
select USERS.user_id, USERS.account, USERS.sex, USERS.id_card, USERS.name, USERS.department_id, s.department_name, USERS.area_code, s2.name area_name, USERS.birthday, USERS.stauts, USERS.last_login_time, USERS.mobile, USERS.email, USERS.seat, USERS. user_level, USERS.create_time, users.fax, users.telphone, users.qq
ORDER BY ${orderBy}
order by substr(USERS.user_id,1,1) desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.insert:
        update USERS_ROLE set role_id='0' where 0=1;
        
            delete from users_role where user_id=#{userId};
        
        
            
                insert into users_role
                (user_id, role_id, create_time)
                values
                (#{userId,jdbcType=VARCHAR}, #{item,jdbcType=VARCHAR}, now());

mapper.select:
select user_id as userId, account, sex, id_card as idCard, name, department_id as departmentId, area_code as areaCode, birthday, stauts, last_login_time as lastLoginTime, ifNUll(mobile, "") as mobile, ifNull(email, "") as email, seat, user_level as userLevel, create_time as createTime, fax, telphone, qq from users
ORDER BY ${orderBy}
order by user_id desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.sql:
where 1=1 and stauts in (0,1)

    and name like concat('%',#{condition.name,jdbcType=VARCHAR},'%')


    and department_Id
    =#{condition.departmentId,jdbcType=VARCHAR}


    and id_Card =#{condition.idCard,jdbcType=VARCHAR}


    and mobile =#{condition.mobile,jdbcType=VARCHAR}


    and area_code like concat(#{condition.areaCode,jdbcType=VARCHAR},'%')


    and account =#{condition.account,jdbcType=VARCHAR}
